    private void GirisButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_GirisButtonActionPerformed

        String query="Select kullanici_adi,parola from kullanici where kullanici_adi='"+loginKullaniciAdiTF.getText()+"'";
        
        try {
        Database db = new Database();
        db.rs=db.st.executeQuery(query);
        while(db.rs.next()){
            if(db.rs.getString(1).equals(loginKullaniciAdiTF.getText()) && db.rs.getString(2).equals(loginParolaTF.getText())){
                
                Ana_Menu menu=new Ana_Menu(loginKullaniciAdiTF.getText());
                menu.setVisible(true);
                setVisible(false);
            }else{
                JOptionPane.showMessageDialog(null, "KULLANICI ADI VEYA PAROLA HATALI!\n TEKRAR DENEYINIZ");
            }
        }

        } catch (SQLException ex) {
            Logger.getLogger(login.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_GirisButtonActionPerformed

