    private void uyeOlButonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_uyeOlButonActionPerformed
        String ad= adTF.getText();
        String soyad =soyadTF.getText();
        String kullaniciAdi=kullaniciAdiTF.getText();
        String parola=parolaTF.getText();
        String parolaTekrar=parolaTekrarTF.getText();
        String query ="INSERT INTO kullanici VALUES (NULL,'"+ad+"','"+soyad+"','"+kullaniciAdi+"','"+parola+"',NULL,NULL,NULL,NULL,NULL,NULL,3)";
        try {
            Database db =new Database();
            if(!(parolaTekrar.equals(parola))){
                JOptionPane.showMessageDialog(null,"PAROLALAR ESLESMIYOR!");
                return;
            }
                  db.st.executeUpdate(query);
                  JOptionPane.showMessageDialog(null, "ÜYELİĞİNİZ BAŞARIYLA GEREÇEKLEŞTİRİLDİ !");
            
          adTF.setText("");
          soyadTF.setText("");
          kullaniciAdiTF.setText("");
          parolaTF.setText("");
          parolaTekrarTF.setText("");
           
        } catch (SQLException ex) {
            Logger.getLogger(login.class.getName()).log(Level.SEVERE, null, ex);
        }
        
        
    }//GEN-LAST:event_uyeOlButonActionPerformed

